mapper.update:
 UPDATE T_SHOP SET 
	 ID=#{id} 
	, SHOP_ID=#{shopId} 
	, SHOP_CODE=#{shopCode} 
	, SHOP_NAME=#{shopName} 
	, COMPANY_ID=#{companyId} 
	, PARENT_ID=#{parentId} 
	, REGION_ID=#{regionId} 
	, COUNTRY=#{country} 
	, CITY=#{city} 
	, SHOP_ICON=#{shopIcon} 
	, ADDRESS=#{address} 
	, OWNER_NAME=#{ownerName} 
	, OWNER_PHONE=#{ownerPhone} 
	, PROMOTER=#{promoter} 
	, ASSISTANT=#{assistant} 
	, MATERIEL_ID=#{materielId} 
	, GRADE_ID=#{gradeId} 
	, BIZ_ID=#{bizId} 
	, TOTAL_NUM=#{totalNum} 
	, CURRENT_NUM=#{currentNum} 
	, CLERK_NUM=#{clerkNum} 
	, BRANDS_FIRST=#{brandsFirst} 
	, BRANDS_TWO=#{brandsTwo} 
	, BRANDS_THREE=#{brandsThree} 
	, COUNTER_NUM=#{counterNum} 
	, DARK_PRO=#{darkPro} 
	, PURCHAS_CHANNEL=#{purchasChannel} 
	, IS_KOM=#{isKom} 
	, RELATIONSHIP=#{relationship} 
	, INCORPORATOR=#{incorporator} 
	, INCORPORATOR_TEL=#{incorporatorTel} 
	, FWIDTH=#{fwidth} 
	, FDEPTH=#{fdepth} 
	, SHOP_AREA=#{shopArea} 
	, SHOW_NUM=#{showNum} 
	, STAFF_NUM=#{staffNum} 
	, SALES_NUM=#{salesNum} 
	, IS_INACTIVE=#{isInactive}
	, STATUS=#{status} 
	, REMARK=#{remark} 
	, UPDATE_BY=#{updateBy} 
	, UPDATE_DATE=str_to_date(#{updateDate},'%Y-%m-%d %H:%i:%s')
	, VERSION=#{version} 
WHERE ID = #{id}

mapper.select:
SELECT
FROM T_SHOP s LEFT JOIN T_COMPANY c ON c.ID = s.COMPANY_ID LEFT JOIN T_REGION r1 ON r1.ID = s.COUNTRY LEFT JOIN T_REGION r2 ON r2.ID = s.CITY LEFT JOIN T_SHOP_EXTENSION e ON s.ID = e.SHOP_ID LEFT JOIN T_SHOP_GRADE g ON s.GRADE_ID = g.ID LEFT JOIN T_ATTRIBUTE a1 ON e.RELATIONSHIP = a1.ID LEFT JOIN T_ATTRIBUTE a2 ON e.BIZ_CATEGORY = a2.ID LEFT JOIN T_ATTRIBUTE a3 ON s.BIZ_ID = a3.ID LEFT JOIN T_MATERIEL m ON m.ID = s.MATERIEL_ID WHERE 1=1
AND s.COMPANY_ID =#{companyId}

SELECT p.ID,p.REGION_ID,p.SHOP_NAME FROM T_SHOP p

JOIN T_USER_SHOP us ON us.SHOP_ID = p.ID
JOIN T_USER u ON u.ID = us.USER_ID 

WHERE 1=1
AND p.IS_INACTIVE = 1
AND p.STATUS = 1
AND p.COMPANY_ID = #{companyId} 
AND p.REGION_ID IS NOT NULL
AND p.REGION_ID = #{regionId}

AND u.USER_CODE = #{userName}

SELECT COUNT(*) FROM T_SHOP p
LEFT JOIN T_USER_SHOP up ON up.SHOP_ID = p.ID
WHERE 1=1
AND p.SHOP_NAME = #{shopName} 
AND up.USER_ID = #{userId}
