			, #{item.updateBy}
			, str_to_date(#{item.updateTime},'%Y-%m-%d %H:%i:%s')
			, #{item.version}
        	)

         ON DUPLICATE KEY UPDATE
		 DAMAGE_ID = VALUES(DAMAGE_ID)
		, SHOP_ID = VALUES(SHOP_ID)
		, MATERIEL_ID = VALUES(MATERIEL_ID)
